function onChange(control, oldValue, newValue, isLoading, isTemplate) {
  	if (isLoading || newValue === '' || newValue == oldValue) {
		return;
	}

   //Type appropriate comment here, and begin script below
   var whatneed = new GlideAjax('WhatNeeded');

	whatneed.addParam('sysparm_name','whatNeed');
	whatneed.addParam('param',newValue);


	//whatneed.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	whatneed.getXML(respdet);


	function respdet(response){
		var resp = response.responseXML.documentElement.getAttribute("answer");

		//g_form.addInfoMessage('Returned value '+ resp);
		/*
		if(resp == 'true')
			{
			g_form.addErrorMessage('What needed updated and script include invoked as true');
			}
		else{
			g_form.addErrorMessage('What needed updated and script include invoked as false: '+ resp);
		}*/
	}

}
